class Ankara{
  constructor(temperature,mood){
    this.temperature=temperature
    this.mood=mood

  }
  predictChanges(){
    const temperature1=20
    const mood1 =5

    if(this.temperature>=temperature1 && this.mood>=mood1){
      console.log("Wear a bright colored ,light Ankara");
    }
    else{
      return ("Wear a dull colored,heavy Ankara")
    }
  }
}
const ankara =new Ankara(30,8)
console.log(ankara.predictChanges())

